.description {
  :global(a) {
    transition: 0.1s border-color;
    border-bottom: 2px solid var(--color-accent);

    &:hover {
      border-color: transparent;
    }
  }
}

ul {
  display: flex;
  align-items: center;

  li:not(:last-child) {
    display: flex;
    align-items: center;
    margin-right: 1rem;

    &:after {
      content: "";
      height: 20px;
      border-right: 2px solid var(--color-accent); 
      padding-right: 1rem;
    }
  }

  a {
    display: flex;
    align-items: center;

    span {
      margin-left: 0.5rem;
    }
  }
}

figure {
  position: relative;
  margin-left: 18px;

  img {
    width: 226px;
    height: 226px;
    border-radius: 50%;
    z-index: 1;
    position: relative;
    object-fit: cover;
  }

  svg {
    position: absolute;
    top: 0;
    left: -18px;
    visibility: hidden;

    circle {
      fill: var(--color-accent);
      transition: 1s transform;
    }

    circle:nth-child(2) {
      filter: brightness(0.75);
    }

    circle:nth-child(3) {
      filter: brightness(0.5);
    }

    &.show {
      visibility: visible; 

      circle:nth-child(1) {
        transform: translateX(-18px);
      }

      circle:nth-child(2) {
        transform: translateX(-12px);
      }

      circle:nth-child(3) {
        transform: translateX(-6px);
      }
    }
  }
}

@media (min-width: 900px) {
  .section-container {
    gap: 6rem;
    grid-template-columns: auto auto;

    & > figure {
      grid-column: 2;
    }

    & > div {
      place-content: center;
      grid-row: 1;
    }
  }
}

